mapper.select:
SELECT
COUNT(1)
FROM js_drawrolls_record a




        and a.id = #{id}


        and a.periods = #{periods,jdbcType=INTEGER}


        and
        su.phone like concat('%',#{phone},'%')


        and name like concat('%',#{name,jdbcType=VARCHAR},'%')


        and sc.id = #{stepId}


        and a.isValid = #{isValid,jdbcType=INTEGER}

SELECT
,,

FROM js_drawrolls_record a




        and a.id = #{id}


        and a.periods = #{periods,jdbcType=INTEGER}


        and
        su.phone like concat('%',#{phone},'%')


        and name like concat('%',#{name,jdbcType=VARCHAR},'%')


        and sc.id = #{stepId}


        and a.isValid = #{isValid,jdbcType=INTEGER}


order by a.status desc, a.lucky_draw desc


        ORDER BY ${page.orderBy}

SELECT COUNT(1)
FROM js_drawrolls_record a
LEFT JOIN js_loan_person b ON a.user_id = b.id
LEFT JOIN js_step_record c ON a.step_id = c.id
LEFT JOIN js_step_config d ON c.step_id = d.id
LEFT JOIN js_award_record e ON a.user_id = e.user_id


        and b.phone = #{phone}


        and a.periods = #{periods,jdbcType=INTEGER}


        and a.isValid = #{isValid,jdbcType=INTEGER}


order by a.add_time desc

SELECT a.id AS id , a.periods AS periods, a.user_id AS userId, a.lucky_draw AS luckyDraw, a.add_time AS ADDTIME, a.update_time AS updateTime, a.status AS STATUS, b.phone AS phone, d.step_name AS stepName, e.award_money AS awardMoney FROM js_drawrolls_record a LEFT JOIN js_loan_person b ON a.user_id = b.id LEFT JOIN js_step_record c ON a.step_id = c.id LEFT JOIN js_step_config d ON c.step_id = d.id LEFT JOIN js_award_record e ON a.user_id = e.user_id
and b.phone = #{phone}
and a.periods = #{periods,jdbcType=INTEGER}
and a.isValid = #{isValid,jdbcType=INTEGER}
order by a.add_time desc

SELECT

FROM js_drawrolls_record a


        and a.periods = #{periods,jdbcType=INTEGER}


        and a.isValid = #{isValid,jdbcType=INTEGER}


order by a.add_time desc limit 0,50

SELECT

FROM js_drawrolls_record a






        ORDER BY ${page.orderBy}
