select
bc.id bankId,
bc.bank_name bankNmae,
uc.user_id userId,
uc.open_name openName,
uc.phone phone,
uc.card_no cardNo,
uc.main_card mainCard,
uc.type type,
uc.status status,
uc.create_time createTime,
uc.is_band isBand,
uc.card_default cardDefault
from user_card_info uc left join bank_all_info bc on (uc.bank_id=bc.id) left join user_info u
on(uc.user_id=u.id)


        uc.user_id=#{userId}


        AND u.open_name=#{userName}


        AND uc.phone=#{userPhone}


        AND uc.card_no=#{cardNo}


        AND uc.status=#{status}


        AND uc.is_band=#{isBand}


        AND uc.create_time>=#{addStartTime} AND uc.create_time<=#{addEndTiem}


        AND uc.create_time>=#{addStartTime}


        AND uc.create_time<=#{addEndTiem}

select count(1) from user_card_info uc left join bank_all_info bc on (uc.bank_id=bc.id) left join user_info u
on(uc.user_id=u.id)


        uc.user_id=#{userId}


        AND u.user_name=#{userName}


        AND uc.phone=#{userPhone}


        AND uc.card_no=#{cardNo}


        AND uc.status=#{status}


        AND uc.is_band=#{isBand}


        AND uc.create_time>=#{addStartTime} AND uc.create_time<=#{addEndTiem}


        AND uc.create_time>=#{addStartTime}


        AND uc.create_time<=#{addEndTiem}

select b.id, b.bank_name bankName, b.card_no card_no, b.phone phone, b.bank_id bank_id, b.type type, b.create_time createTime, b.open_name openName, b.main_card mainCard, b.agreeno, b.is_band isBand, b.card_default cardDefault from user_card_info b where user_id=#{userId}
and type = #{type}
and status = #{status}
limit #{limit}

select uc.id, uc.user_id userId, uc.open_name openName, uc.phone phone, uc.card_no card_no, uc.main_card mainCard, uc.type type, uc.status status, uc.create_time createTime, uc.is_band isBand, uc.card_default cardDefault from user_card_info uc WHERE uc.is_band=#{isBand}
and status = #{status}
and user_id = #{userId}
and card_default = 1 ORDER BY uc.`id` ASC LIMIT #{page},#{pageSize}
