mapper.update:
update user_borrowquota_snapshot
set borrow_product_id = #{productId} ,
    update_time = now(),
    user_amount_limit = #{nowLimit}
where user_id = #{userId} and borrow_day = #{borrowDay}

mapper.select:
SELECT MAX(user_amount_limit)
FROM  user_borrowquota_snapshot
WHERE user_id = #{userId}

select id, user_id, borrow_product_id, deal_flag, remark,user_amount_limit,borrow_day,update_time
from user_borrowquota_snapshot
where user_id = #{userId} and deal_flag = 'n' and borrow_day = #{borrowDay}
