mapper.select:
select uc.id userId, uc.realname realname, uc.id_number idNumber, uc.create_time as createTime, uc.update_time as updateTime from user_info uc
uc.realname_status=1
AND uc.id=#{userId}
AND uc.realname=#{realname}
AND uc.id_number=#{idNumber}

select count(1) from user_info uc

	uc.realname_status=1

 		AND uc.id=#{userId}


 		AND uc.realname=#{realname}


 		AND uc.id_number=#{idNumber}

select uc.id userId, uc.realname realname, uc.id_number idNumber, uc.user_phone userPhone, uc.pay_password payPassWord, uc.realname_status realNameStatus, uc.taobao_account taobaoAccount, uc.qq qq, uc.email email, uc.wechat_account, uc.zm_status zmStatus, uc.jxl_status jxlStatus, uc.td_status tdStatus, uc.my_hb_time myhbtime, IF(card.id IS NULL,0,1) bankId, IF(company.userId IS NULL,0,1) companyInfo, IF(emergency.userId IS NULL,0,1) emergencyInfo from user_info uc LEFT JOIN user_card_info card ON(card.user_id=uc.id) LEFT JOIN (select u.id userId from user_info u where u.company_address IS NOT null AND u.company_address_distinct IS NOT NULL AND u.company_name IS NOT NULL AND u.company_period IS NOT null AND u.company_phone IS NOT null ) company ON(uc.id=company.userId) LEFT JOIN (select u.id userId from user_info u where u.first_contact_name IS NOT NULL AND u.first_contact_phone IS NOT NULL AND u.second_contact_name IS NOT null AND u.second_contact_phone IS NOT NULL) emergency ON(uc.id=emergency.userId)
uc.id=#{userId}
AND uc.realname=#{realname}
AND uc.user_phone=#{userPhone}

select count(1) from user_info uc LEFT JOIN user_card_info card ON(card.user_id=uc.id) LEFT JOIN (select u.id userId from user_info u where u.company_address IS NOT null AND u.company_address_distinct IS NOT NULL AND u.company_name IS NOT NULL AND u.company_period IS NOT null AND u.company_phone IS NOT null ) company ON(uc.id=company.userId) LEFT JOIN (select u.id userId from user_info u where u.first_contact_name IS NOT NULL AND u.first_contact_phone IS NOT NULL AND u.second_contact_name IS NOT null AND u.second_contact_phone IS NOT NULL) emergency ON(uc.id=emergency.userId)
uc.id=#{userId}
AND uc.realname=#{realname}
AND uc.user_phone=#{userPhone}

select
user_Phone as userPhone,
id_number as idNumber
from user_info where 1=1

	and id = #{id}


	and user_phone=#{userPhone}


	and id_number=#{idNumber}

select count(1) from user_info where 1=1 

 and id = #{id}


 and realname = #{realname}


 and id_number = #{idNumber}


and user_phone = #{userPhone}


 and create_time>=#{createTime} AND create_time<=#{beginTime}


 and create_time>=#{createTime}


 and create_time<=#{beginTime}


 and status=#{status}

 and status not in (3)
order by create_time desc

select 
	id as id,
	user_name as userName,
	user_phone as userPhone,
	realname as realname,
	realname_status as realnameStatus,
	id_number as idNumber,
	status as status,
	create_time as createTime,
	user_sex as userSex
from user_info where 1=1 

 and id = #{id}


 and realname = #{realname}


 and id_number = #{idNumber}


and user_phone = #{userPhone}


 and create_time>=#{createTime} AND create_time<=#{beginTime}


 and create_time>=#{createTime}


 and create_time<=#{beginTime}


 and status=#{status}

 and status not in (3)
order by create_time desc

